// src/firebaseConfig.ts
import { initializeApp } from "firebase/app";
import { getAuth, GoogleAuthProvider, signInWithPopup, signOut } from "firebase/auth";

// Sua configuração do Firebase
const firebaseConfig = {
  apiKey: "AIzaSyDXNP1zHmzZX7BbGZ_2H1jzMZVfS4uX9Wc",
  authDomain: "gajyun-portal.firebaseapp.com",
  projectId: "gajyun-portal",
  storageBucket: "gajyun-portal.appspot.com",
  messagingSenderId: "206621290390",
  appId: "1:206621290390:web:fe8ac7fa8d0b78ec7ab7b7",
  measurementId: "G-2Q8G0FTL7G"
};

// Inicializar o Firebase
const app = initializeApp(firebaseConfig);
const auth = getAuth(app);
const provider = new GoogleAuthProvider();

const signInWithGoogle = () => {
  return signInWithPopup(auth, provider);
};

const logout = () => {
  return signOut(auth);
};

export { auth, signInWithGoogle, logout };
